CREATE TABLE blocks (
      id TEXT PRIMARY KEY,
      page_id TEXT NOT NULL,
      site_id TEXT NOT NULL,
      type TEXT NOT NULL,
      position INTEGER NOT NULL,
      visible BOOLEAN DEFAULT 1,
      settings TEXT NOT NULL, -- JSON
      created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
      updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
    );
CREATE TABLE widget_definitions (
        id TEXT PRIMARY KEY,
        name TEXT NOT NULL,
        description TEXT,
        category TEXT NOT NULL,
        version TEXT NOT NULL DEFAULT '1.0.0',
        template TEXT NOT NULL,
        styles TEXT,
        script TEXT,
        admin_fields TEXT NOT NULL, -- JSON
        triggers TEXT,              -- JSON
        integrations TEXT,          -- JSON
        active BOOLEAN DEFAULT 1,
        global BOOLEAN DEFAULT 1,
        created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
        updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
      );
INSERT INTO widget_definitions VALUES('email-capture','Email Capture','Collect email addresses with customizable lead magnets','conversion','1.0.0',replace('\n      <div class="email-capture-widget" style="{{container_styles}}">\n        <h3>{{title}}</h3>\n        <p>{{description}}</p>\n        <form class="email-form" onsubmit="submitEmail(event)">\n          <input type="email" placeholder="{{email_placeholder}}" required>\n          <button type="submit" style="{{button_styles}}">{{button_text}}</button>\n        </form>\n        <p class="privacy-text">{{privacy_text}}</p>\n      </div>\n    ','\n',char(10)),replace('\n      .email-capture-widget {\n        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);\n        padding: 2rem;\n        border-radius: 12px;\n        color: white;\n        text-align: center;\n        max-width: 500px;\n        margin: 2rem auto;\n      }\n      .email-form input {\n        width: 100%;\n        padding: 12px;\n        border: none;\n        border-radius: 6px;\n        margin-bottom: 1rem;\n      }\n      .privacy-text {\n        font-size: 0.8rem;\n        opacity: 0.8;\n      }\n    ','\n',char(10)),replace('\n      function submitEmail(event) {\n        event.preventDefault();\n        const email = event.target.querySelector(''input[type="email"]'').value;\n        \n        // Submit to API\n        fetch(''/api/emails'', {\n          method: ''POST'',\n          headers: { ''Content-Type'': ''application/json'' },\n          body: JSON.stringify({ email, source: ''email-capture-widget'' })\n        }).then(() => {\n          alert(''Thanks for signing up!'');\n          event.target.reset();\n        });\n      }\n    ','\n',char(10)),'[{"key":"title","label":"Title","type":"text","required":true,"defaultValue":"Get Free Access"},{"key":"description","label":"Description","type":"textarea","required":true,"defaultValue":"Join thousands getting our free resources!"},{"key":"button_text","label":"Button Text","type":"text","required":true,"defaultValue":"Subscribe Now"},{"key":"email_placeholder","label":"Email Placeholder","type":"text","required":false,"defaultValue":"Enter your email address"},{"key":"privacy_text","label":"Privacy Text","type":"text","required":false,"defaultValue":"No spam, ever. Unsubscribe anytime."},{"key":"button_styles","label":"Button Styles","type":"text","required":false,"defaultValue":"background: #ff6b6b; color: white; border: none; padding: 12px 24px; border-radius: 6px; cursor: pointer;"},{"key":"container_styles","label":"Container Styles","type":"text","required":false,"defaultValue":""}]','[{"event":"page_load","delay":2000}]','[{"type":"email_capture","config":{"list":"main"}}]',1,1,'2025-11-26 02:16:38','2025-11-26 02:16:38');
INSERT INTO widget_definitions VALUES('exit-intent-popup','Exit Intent Popup','Show popup when user is about to leave the page','conversion','1.0.0',replace('\n      <div id="exit-popup" class="exit-popup-overlay" style="display: none;">\n        <div class="exit-popup-content">\n          <button class="close-btn" onclick="closeExitPopup()">&times;</button>\n          <h2>{{headline}}</h2>\n          <p>{{message}}</p>\n          <form onsubmit="submitExitEmail(event)">\n            <input type="email" placeholder="{{email_placeholder}}" required>\n            <button type="submit">{{button_text}}</button>\n          </form>\n        </div>\n      </div>\n    ','\n',char(10)),replace('\n      .exit-popup-overlay {\n        position: fixed;\n        top: 0;\n        left: 0;\n        width: 100%;\n        height: 100%;\n        background: rgba(0,0,0,0.8);\n        z-index: 9999;\n        display: flex;\n        align-items: center;\n        justify-content: center;\n      }\n      .exit-popup-content {\n        background: white;\n        padding: 2rem;\n        border-radius: 12px;\n        max-width: 500px;\n        position: relative;\n        text-align: center;\n      }\n      .close-btn {\n        position: absolute;\n        top: 10px;\n        right: 15px;\n        border: none;\n        background: none;\n        font-size: 24px;\n        cursor: pointer;\n      }\n    ','\n',char(10)),replace('\n      let exitIntentShown = false;\n      \n      document.addEventListener(''mouseleave'', function(e) {\n        if (e.clientY <= 0 && !exitIntentShown) {\n          document.getElementById(''exit-popup'').style.display = ''flex'';\n          exitIntentShown = true;\n        }\n      });\n      \n      function closeExitPopup() {\n        document.getElementById(''exit-popup'').style.display = ''none'';\n      }\n      \n      function submitExitEmail(event) {\n        event.preventDefault();\n        const email = event.target.querySelector(''input[type="email"]'').value;\n        \n        fetch(''/api/emails'', {\n          method: ''POST'',\n          headers: { ''Content-Type'': ''application/json'' },\n          body: JSON.stringify({ email, source: ''exit-intent-popup'' })\n        }).then(() => {\n          closeExitPopup();\n          alert(''Thanks! Check your email for your free resource.'');\n        });\n      }\n    ','\n',char(10)),'[{"key":"headline","label":"Headline","type":"text","required":true,"defaultValue":"Wait! Before You Go..."},{"key":"message","label":"Message","type":"textarea","required":true,"defaultValue":"Get our free guide delivered to your inbox!"},{"key":"button_text","label":"Button Text","type":"text","required":true,"defaultValue":"Get Free Guide"},{"key":"email_placeholder","label":"Email Placeholder","type":"text","required":false,"defaultValue":"Your email address"}]','[{"event":"exit_intent"}]','[{"type":"email_capture","config":{"list":"exit-intent"}}]',1,1,'2025-11-26 02:16:38','2025-11-26 02:16:38');
CREATE TABLE widget_instances (
        id TEXT PRIMARY KEY,
        widget_id TEXT NOT NULL,
        site_id TEXT NOT NULL,
        page_id TEXT,
        position INTEGER NOT NULL DEFAULT 0,
        settings TEXT NOT NULL DEFAULT '{}', -- JSON
        active BOOLEAN DEFAULT 1,
        created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
        updated_at DATETIME DEFAULT CURRENT_TIMESTAMP,
        FOREIGN KEY (widget_id) REFERENCES widget_definitions(id)
      );
CREATE INDEX idx_media_site ON media (site_id);
CREATE INDEX idx_media_file_type ON media (file_type);
CREATE INDEX idx_media_created_at ON media (created_at);
CREATE INDEX idx_blocks_page_site ON blocks(page_id, site_id);
CREATE INDEX idx_blocks_position ON blocks(position);
CREATE INDEX idx_widget_instances_site ON widget_instances(site_id);
CREATE INDEX idx_widget_instances_page ON widget_instances(page_id);
CREATE INDEX idx_widget_definitions_category ON widget_definitions(category);
COMMIT;
